import React, { useEffect, useState } from 'react';
import { Typography, Box } from '@mui/material';

function Labels({ jsonData }) {
  const [labels, setLabels] = useState([]);
  const [totalLabels, setTotalLabels] = useState(0);
  const [totalAnnotations, setTotalAnnotations] = useState(0);

  useEffect(() => {
    const categoryCount = {};
    jsonData.annotations.forEach((annotation) => {
      const categoryName = jsonData.categories.find((cat) => cat.id === annotation.category_id).name;
      categoryCount[categoryName] = (categoryCount[categoryName] || 0) + 1;
    });
    setLabels(Object.entries(categoryCount).map(([label, count]) => ({ label, count })));
    setTotalLabels(Object.keys(categoryCount).length);
    setTotalAnnotations(jsonData.annotations.length);
  }, [jsonData]);

  return (
    <Box>
      <Typography variant="h5">Labels</Typography>
      <Typography>Total Unique Labels: {totalLabels}</Typography>
      <Typography>Total Annotations: {totalAnnotations}</Typography>
      <ul>
        {labels.map((label) => (
          <li key={label.label}>
            <Typography>
              <strong>{label.label}:</strong> {label.count}
            </Typography>
          </li>
        ))}
      </ul>
    </Box>
  );
}

export default Labels;
