import React, { useState } from 'react';
import axios from 'axios';

function RemoveLabels() {
    const [labels, setLabels] = useState('');
    const [file, setFile] = useState(null); // New state for file input
    const [errorMessage, setErrorMessage] = useState('');

    const handleFileChange = (e) => {
        setFile(e.target.files[0]); // Set file from input
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!file) {
            setErrorMessage('Please select a file before submitting.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file); // Add the file to the form data
        formData.append('categories_to_remove', labels);

        try {
            const response = await axios.post('http://localhost:5000/remove_labels', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log(response.data); // Handle success (you can display the success message or file link here)
        } catch (error) {
            console.error('There was an error removing the labels:', error);
            setErrorMessage(`There was an error removing the labels: ${error.response?.data?.error || error.message}`);
        }
    };

    return (
        <div>
            <h2>Remove Labels</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Labels to remove (comma-separated)</label>
                    <input
                        type="text"
                        value={labels}
                        onChange={(e) => setLabels(e.target.value)}
                    />
                </div>
                <div>
                    <label>Upload JSON File</label>
                    <input type="file" onChange={handleFileChange} />
                </div>
                <button type="submit">REMOVE LABELS</button>
            </form>
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </div>
    );
}

export default RemoveLabels;
