import React, { useState } from 'react';
import axios from 'axios';

function RemoveLabels() {
    const [labels, setLabels] = useState(''); // Labels to remove
    const [file, setFile] = useState(null); // For JSON file
    const [errorMessage, setErrorMessage] = useState(''); // Error messages
    const [downloadUrl, setDownloadUrl] = useState(''); // URL for the download

    // Handle file input change
    const handleFileChange = (e) => {
        setFile(e.target.files[0]); // Set file in state
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Check if file exists
        if (!file) {
            setErrorMessage('Please select a file before submitting.');
            return;
        }

        // Prepare the form data for sending to the server
        const formData = new FormData();
        formData.append('file', file); // Append the file
        formData.append('categories_to_remove', labels); // Append the labels to remove

        try {
            const response = await axios.post('http://localhost:5000/remove_labels', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            // On success, set the download URL from the response
            setDownloadUrl(response.data.file_url);
        } catch (error) {
            // Handle the error if the request fails
            setErrorMessage(`There was an error removing the labels: ${error.response?.data?.error || error.message}`);
        }
    };

    return (
        <div>
            <h2>Remove Labels</h2>
            <form onSubmit={handleSubmit}>
                {/* Labels input */}
                <div>
                    <label>Labels to remove (comma-separated)</label>
                    <input
                        type="text"
                        value={labels}
                        onChange={(e) => setLabels(e.target.value)}
                        placeholder="e.g., Sunroof, Hand Brake Button"
                    />
                </div>

                {/* File input */}
                <div>
                    <label>Upload JSON File</label>
                    <input type="file" onChange={handleFileChange} />
                </div>

                {/* Submit button */}
                <button type="submit">REMOVE LABELS</button>
            </form>

            {/* Error message */}
            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

            {/* Show download button when a file is ready */}
            {downloadUrl && (
                <div>
                    <a href={`http://localhost:5000${downloadUrl}`} download>
                        <button>Download Modified JSON</button>
                    </a>
                </div>
            )}
        </div>
    );
}

export default RemoveLabels;
