import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';

function Search({ jsonData }) {
  const [label, setLabel] = useState('');
  const [results, setResults] = useState({});
  const [imageCount, setImageCount] = useState(0);

  const handleSearch = (e) => {
    e.preventDefault();
    const searchResults = {};
    let count = 0;

    jsonData.annotations.forEach((annotation) => {
      const categoryName = jsonData.categories.find((cat) => cat.id === annotation.category_id).name;
      if (categoryName.toLowerCase().includes(label.toLowerCase())) {
        const imageId = annotation.image_id;
        const fileName = jsonData.images.find((img) => img.id === imageId)?.file_name || 'Unknown';
        if (!searchResults[imageId]) {
          searchResults[imageId] = { file_name: fileName, labels: [categoryName] };
          count += 1;
        } else {
          searchResults[imageId].labels.push(categoryName);
        }
      }
    });

    setResults(searchResults);
    setImageCount(count);
  };

  return (
    <Box>
      <Typography variant="h5">Search Labels</Typography>
      <form onSubmit={handleSearch}>
        <TextField
          label="Search Label"
          value={label}
          onChange={(e) => setLabel(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" type="submit">
          Search
        </Button>
      </form>

      {/* Display summary information */}
      {imageCount > 0 && (
        <Typography variant="h6" sx={{ mt: 2 }}>
          Label "{label}" found in {imageCount} images.
        </Typography>
      )}

      <ul>
        {Object.keys(results).map((imageId) => (
          <li key={imageId}>
            <Typography>
              <strong>Image ID:</strong> {imageId}, 
              <strong>File Name:</strong> {results[imageId].file_name}, 
              <strong>Labels:</strong> {results[imageId].labels.join(', ')}
            </Typography>
          </li>
        ))}
      </ul>
    </Box>
  );
}

export default Search;
