import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import axios from 'axios';

function MergeLabels({ jsonData, setJsonData }) {
  const [labelsToMerge, setLabelsToMerge] = useState('');
  const [mergedLabel, setMergedLabel] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [downloadLink, setDownloadLink] = useState(null);

  const handleMerge = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('annotations_to_merge', labelsToMerge);
    formData.append('merged_category_name', mergedLabel);
    formData.append('file', jsonData);

    axios.post('http://localhost:5000/merge_labels', formData)
      .then(response => {
        setSuccessMessage('Labels merged successfully.');
        setDownloadLink(response.data.file_url);
        setErrorMessage('');  // Clear any previous error
      })
      .catch(error => {
        setErrorMessage('There was an error merging the labels.');
        console.error('Error:', error.response ? error.response.data : error);
      });
  };

  return (
    <Box mt={3}>
      <Typography variant="h5">Merge Labels</Typography>
      <form onSubmit={handleMerge}>
        <TextField
          label="Labels to merge (comma-separated)"
          value={labelsToMerge}
          onChange={(e) => setLabelsToMerge(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <TextField
          label="Merged Label"
          value={mergedLabel}
          onChange={(e) => setMergedLabel(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" type="submit">
          Merge Labels
        </Button>
      </form>

      {successMessage && (
        <Typography variant="body1" color="success" sx={{ mt: 2 }}>
          {successMessage}
        </Typography>
      )}

      {errorMessage && (
        <Typography variant="body1" color="error" sx={{ mt: 2 }}>
          {errorMessage}
        </Typography>
      )}

      {downloadLink && (
        <Box mt={3}>
          <Button variant="contained" color="primary" href={downloadLink} download>
            Download Modified JSON
          </Button>
        </Box>
      )}
    </Box>
  );
}

export default MergeLabels;
