import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import axios from 'axios';

function RemoveLabels({ jsonData, setJsonData }) {
  const [labelsToRemove, setLabelsToRemove] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [revisedLabels, setRevisedLabels] = useState([]);

  const handleRemove = (e) => {
    e.preventDefault();
    const labelsArray = labelsToRemove.split(',').map(label => label.trim());

    axios.post('http://localhost:5000/remove_labels', { categories_to_remove: labelsArray, data: jsonData })
      .then(response => {
        setJsonData(response.data.data); // Update the global modified JSON
        setSuccessMessage('Labels removed successfully.');
        setRevisedLabels(response.data.data.categories); // Store revised labels
      })
      .catch(error => {
        console.error('There was an error removing the labels!', error);
      });
  };

  return (
    <Box mt={3}>
      <Typography variant="h5">Remove Labels</Typography>
      <form onSubmit={handleRemove}>
        <TextField
          label="Labels to remove (comma-separated)"
          value={labelsToRemove}
          onChange={(e) => setLabelsToRemove(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="secondary" type="submit">
          Remove Labels
        </Button>
      </form>

      {successMessage && (
        <Typography variant="body1" color="success" sx={{ mt: 2 }}>
          {successMessage}
        </Typography>
      )}

      {revisedLabels.length > 0 && (
        <Box mt={3}>
          <Button variant="contained" color="primary" onClick={() => console.log(revisedLabels)}>
            View Revised Labels
          </Button>
          <ul>
            {revisedLabels.map(label => (
              <li key={label.id}>
                <Typography>{label.name}</Typography>
              </li>
            ))}
          </ul>
        </Box>
      )}
    </Box>
  );
}

export default RemoveLabels;
