import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import axios from 'axios';

function RenameLabels({ jsonData, setJsonData }) {
  const [renameMapping, setRenameMapping] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [revisedLabels, setRevisedLabels] = useState([]);

  const handleRename = (e) => {
    e.preventDefault();
    const mappingObject = renameMapping.split(',').reduce((acc, pair) => {
      const [oldLabel, newLabel] = pair.split(':').map(s => s.trim());
      acc[oldLabel] = newLabel;
      return acc;
    }, {});

    axios.post('http://localhost:5000/rename_labels', { rename_mapping: mappingObject, data: jsonData })
      .then(response => {
        setJsonData(response.data.data); // Update the global modified JSON
        setSuccessMessage('Labels renamed successfully.');
        setRevisedLabels(response.data.data.categories); // Store revised labels
      })
      .catch(error => {
        console.error('There was an error renaming the labels!', error);
      });
  };

  return (
    <Box mt={3}>
      <Typography variant="h5">Rename Labels</Typography>
      <form onSubmit={handleRename}>
        <TextField
          label="Rename Mapping (oldLabel:newLabel, ...)"
          value={renameMapping}
          onChange={(e) => setRenameMapping(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" type="submit">
          Rename Labels
        </Button>
      </form>

      {successMessage && (
        <Typography variant="body1" color="success" sx={{ mt: 2 }}>
          {successMessage}
        </Typography>
      )}

      {revisedLabels.length > 0 && (
        <Box mt={3}>
          <Button variant="contained" color="primary" onClick={() => console.log(revisedLabels)}>
            View Revised Labels
          </Button>
          <ul>
            {revisedLabels.map(label => (
              <li key={label.id}>
                <Typography>{label.name}</Typography>
              </li>
            ))}
          </ul>
        </Box>
      )}
    </Box>
  );
}

export default RenameLabels;
