import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import axios from 'axios';

function RenameLabels({ jsonData, setJsonData }) {
  const [renameMapping, setRenameMapping] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [downloadLink, setDownloadLink] = useState(null);

  const handleRename = (e) => {
    e.preventDefault();
    const mappingObject = renameMapping.split(',').reduce((acc, pair) => {
      const [oldLabel, newLabel] = pair.split(':').map(s => s.trim());
      if (oldLabel && newLabel) acc[oldLabel] = newLabel;
      return acc;
    }, {});

    if (Object.keys(mappingObject).length === 0) {
      setErrorMessage('Invalid mapping format. Use oldLabel:newLabel format.');
      return;
    }

    const formData = new FormData();
    formData.append('rename_mapping', JSON.stringify(mappingObject));
    formData.append('file', jsonData);

    axios.post('http://localhost:5000/rename_labels', formData)
      .then(response => {
        setSuccessMessage('Labels renamed successfully.');
        setDownloadLink(response.data.file_url);
        setErrorMessage('');  // Clear any previous error
      })
      .catch(error => {
        setErrorMessage('There was an error renaming the labels.');
        console.error('Error:', error.response ? error.response.data : error);
      });
  };

  return (
    <Box mt={3}>
      <Typography variant="h5">Rename Labels</Typography>
      <form onSubmit={handleRename}>
        <TextField
          label="Rename Mapping (oldLabel:newLabel, ...)"
          value={renameMapping}
          onChange={(e) => setRenameMapping(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" type="submit">
          Rename Labels
        </Button>
      </form>

      {successMessage && (
        <Typography variant="body1" color="success" sx={{ mt: 2 }}>
          {successMessage}
        </Typography>
      )}

      {errorMessage && (
        <Typography variant="body1" color="error" sx={{ mt: 2 }}>
          {errorMessage}
        </Typography>
      )}

      {downloadLink && (
        <Box mt={3}>
          <Button variant="contained" color="primary" href={downloadLink} download>
            Download Modified JSON
          </Button>
        </Box>
      )}
    </Box>
  );
}

export default RenameLabels;
