import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import axios from 'axios';

function MergeLabels({ jsonData, setJsonData }) {
  const [annotationsToMerge, setAnnotationsToMerge] = useState('');
  const [mergedCategory, setMergedCategory] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [revisedLabels, setRevisedLabels] = useState([]);

  const handleMerge = (e) => {
    e.preventDefault();
    const annotationsArray = annotationsToMerge.split(',').map(label => label.trim());

    axios.post('http://localhost:5000/merge_labels', { annotations_to_merge: annotationsArray, merged_category_name: mergedCategory, data: jsonData })
      .then(response => {
        setJsonData(response.data.data); // Update the global modified JSON
        setSuccessMessage('Labels merged successfully.');
        setRevisedLabels(response.data.data.categories); // Store revised labels
      })
      .catch(error => {
        console.error('There was an error merging the labels!', error);
      });
  };

  return (
    <Box mt={3}>
      <Typography variant="h5">Merge Labels</Typography>
      <form onSubmit={handleMerge}>
        <TextField
          label="Annotations to merge (comma-separated)"
          value={annotationsToMerge}
          onChange={(e) => setAnnotationsToMerge(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <TextField
          label="Merged Category Name"
          value={mergedCategory}
          onChange={(e) => setMergedCategory(e.target.value)}
          variant="outlined"
          fullWidth
          margin="normal"
        />
        <Button variant="contained" color="primary" type="submit">
          Merge Labels
        </Button>
      </form>

      {successMessage && (
        <Typography variant="body1" color="success" sx={{ mt: 2 }}>
          {successMessage}
        </Typography>
      )}

      {revisedLabels.length > 0 && (
        <Box mt={3}>
          <Button variant="contained" color="primary" onClick={() => console.log(revisedLabels)}>
            View Revised Labels
          </Button>
          <ul>
            {revisedLabels.map(label => (
              <li key={label.id}>
                <Typography>{label.name}</Typography>
              </li>
            ))}
          </ul>
        </Box>
      )}
    </Box>
  );
}

export default MergeLabels;
